pragma solidity 0.6.8;

import "./BancorFormula.sol";
import "../interfaces/IBondingCurve.sol";


abstract contract BancorBondingCurve is IBondingCurve, BancorFormula {
    uint32 public reserveRatio;

    constructor(uint32 _reserveRatio) public {
        reserveRatio = _reserveRatio;
    }

    function getMintReward(uint _depositTokenAmount) override public view returns (uint) {
        return calculatePurchaseReturn(Supply(), reserveBalance(), reserveRatio, _depositTokenAmount);
    }

    function getBurnRefund(uint _bondingTokenAmount) override public view returns (uint) {
        return calculateSaleReturn(Supply(), reserveBalance(), reserveRatio, _bondingTokenAmount);
    }

    /**
    * @dev Abstract method that returns token supply
    */
    function Supply() virtual public view returns (uint);

    /**
    * @dev Abstract method that returns reserve token balance
    */
    function reserveBalance() virtual public view returns (uint);
}
